import React from 'react';
import { getAll } from './actions';
import { connect } from 'react-redux';
import { compose } from 'redux';
import { createStructuredSelector } from 'reselect';
import SelectIndexState from './selectors';

interface IIndexProps {
  dispatch: Function,
  IndexState: any
}
class Index extends React.Component<IIndexProps> {
  constructor(props: IIndexProps) {
    super(props);
  }
  componentDidMount() {
    this.props.dispatch(getAll())
  }
  public render(): JSX.Element {
    console.log(this.props.IndexState)
    return (
      <div>
        <h2>{this.props.IndexState.posts[0].title}</h2>
        <p>{this.props.IndexState.posts[0].body}</p>
      </div>
    )
  }
}



const mapStateToProps = createStructuredSelector<any, any>({
  IndexState: SelectIndexState()
});

function mapDispatchToProps(dispatch: any) {
  return {
    dispatch,
  };
}

const withConnect = connect(
  mapStateToProps,
  mapDispatchToProps,
);

export default compose(withConnect)(Index);
